import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import API_BASE_URL from '../../../apiConfigure';
import { motion } from 'framer-motion';

interface ChatProps {
  groupId: string;
  userType: string;
  isMember: boolean;
}

interface Message {
  id: string;
  sender_name: string;
  content: string;
  timestamp: string;
}

const Chat: React.FC<ChatProps> = ({ groupId, userType, isMember }) => {
  const [messages, setMessages] = useState<Message[]>([]);
  const [newMessage, setNewMessage] = useState('');
  const messagesEndRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const fetchMessages = async () => {
      const response = await axios.get<Message[]>(`${API_BASE_URL}/groups/${groupId}/messages/`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('access_token')}`,
        },
      });
      setMessages(response.data);
    };
    fetchMessages();
  }, [groupId]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSendMessage = async () => {
    if (!newMessage.trim()) return;
    try {
      const response = await axios.post<Message>(
        `${API_BASE_URL}/groups/${groupId}/messages/`,
        { content: newMessage },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('access_token')}`,
          },
        }
      );
      setMessages([...messages, response.data]);
      setNewMessage('');
    } catch (error) {
      alert('Failed to send message. Are you a member and a student?');
    }
  };

  if (userType !== 'student') {
    return <div className="text-red-500">Only students can participate in group chats.</div>;
  }

  if (!isMember) {
    return <div className="text-blue-500">Join this group to view and send messages.</div>;
  }

  return (
    <div className="bg-white shadow-md rounded-lg p-6 flex flex-col h-full">
      <h1 className="text-2xl font-bold mb-4">Group Chat</h1>
      <div className="flex-1 overflow-y-auto space-y-4">
        {messages.map((message) => (
          <motion.div
            key={message.id}
            className="p-4 bg-gray-100 rounded-lg shadow"
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
          >
            <div className="font-semibold">{message.sender_name}</div>
            <div>{message.content}</div>
            <div className="text-xs text-gray-400">{new Date(message.timestamp).toLocaleTimeString()}</div>
          </motion.div>
        ))}
        <div ref={messagesEndRef} />
      </div>
      <div className="mt-4 flex items-center space-x-2">
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="Type a message..."
          className="flex-1 p-2 border rounded"
          onKeyDown={e => e.key === 'Enter' && handleSendMessage()}
        />
        <button onClick={handleSendMessage} className="btn-primary">
          Send
        </button>
      </div>
    </div>
  );
};

export default Chat;
