import React from 'react';
import { motion } from 'framer-motion';
import '../../styles/Sidebar.css'; // Import the CSS

const Sidebar = ({ setActiveSection }: { setActiveSection: (section: string) => void }) => {
  return (
    <div className="w-64 bg-gray-800 text-white flex flex-col shadow-lg">
      <div className="p-6 border-b border-gray-700">
        <h2 className="text-xl font-bold">Campus Cartel</h2>
      </div>
      <nav className="flex-1">
        <motion.button
          onClick={() => setActiveSection('profile')}
          className="w-full text-left px-6 py-4 hover:bg-gray-700 transition duration-300"
          whileHover={{ scale: 1.05 }}
        >
          Profile
        </motion.button>
        <motion.button
          onClick={() => setActiveSection('posts')}
          className="w-full text-left px-6 py-4 hover:bg-gray-700 transition duration-300"
          whileHover={{ scale: 1.05 }}
        >
          Posts
        </motion.button>
        <motion.button
          onClick={() => setActiveSection('groups')}
          className="w-full text-left px-6 py-4 hover:bg-gray-700 transition duration-300"
          whileHover={{ scale: 1.05 }}
        >
          Groups
        </motion.button>
      </nav>
    </div>
  );
};

export default Sidebar;
