import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import API_BASE_URL from '../apiConfigure';
import { PencilIcon } from '@heroicons/react/24/outline';
import PostCard from '../components/posts/PostCard';
import { motion } from 'framer-motion';
import '../styles/Profile.css';


const Profile = () => {
  const { id } = useParams();
  const { user } = useAuth();

  interface ProfileData {
    firstname: string;
    lastname: string;
    bio: string;
    avatar?: string;
    coverPhoto?: string;
  }

  interface Post {
    id: number;
    title: string;
    content: string;
    like_count: number;
    comment_count: number;
    liked: boolean;
  }

  const [profile, setProfile] = useState<ProfileData | null>(null);
  const [posts, setPosts] = useState<Post[]>([]);
  const [isFollowing, setIsFollowing] = useState(false);
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await axios.get<ProfileData>(`${API_BASE_URL}/users/${id}/`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` },
        });
        setProfile(response.data);
      } catch (error) {
        console.error('Error fetching profile:', error);
      }
    };

    fetchProfile();
  }, [id]);

  const handleFollow = async () => {
    try {
      const endpoint = isFollowing
        ? `${API_BASE_URL}/users/${id}/unfollow/`
        : `${API_BASE_URL}/users/${id}/follow/`;
      await axios.post(endpoint, {}, {
        headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` },
      });
      setIsFollowing(!isFollowing);
    } catch (error) {
      console.error('Error following/unfollowing:', error);
    }
  };

  if (!profile) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-blue-500 border-solid"></div>
      </div>
    );
  }

  return (
    <motion.div
      className="max-w-4xl mx-auto p-6 bg-white shadow-md rounded-lg"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      {/* Cover Photo */}
      <motion.div
        className="relative h-48 bg-gray-200 rounded-lg overflow-hidden"
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <img
          src={profile.coverPhoto || 'https://via.placeholder.com/800x200'}
          alt="Cover"
          className="w-full h-full object-cover"
        />
        <button
          className="absolute top-2 right-2 bg-black/50 text-white p-2 rounded-full hover:bg-black/70"
          onClick={() => setIsEditing(true)}
        >
          <PencilIcon className="h-5 w-5" />
        </button>
      </motion.div>

      {/* Profile Picture and Info */}
      <motion.div
        className="flex items-center mt-4"
        initial={{ opacity: 0, x: -20 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 0.5 }}
      >
        <img
          src={profile.avatar || 'https://via.placeholder.com/150'}
          alt="Profile"
          className="h-24 w-24 rounded-full border-4 border-white shadow-md"
        />
        <div className="ml-4">
          <h1 className="text-2xl font-bold">{profile.firstname} {profile.lastname}</h1>
          <p className="text-gray-600">{profile.bio}</p>
        </div>
        <div className="ml-auto">
          <motion.button
            onClick={handleFollow}
            className={`px-4 py-2 rounded-lg ${
              isFollowing ? 'bg-red-500 hover:bg-red-600' : 'bg-blue-500 hover:bg-blue-600'
            } text-white`}
            whileHover={{ scale: 1.05 }}
          >
            {isFollowing ? 'Unfollow' : 'Follow'}
          </motion.button>
        </div>
      </motion.div>

      {/* Posts Section */}
      <motion.div
        className="mt-6"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <h2 className="text-xl font-bold mb-4">Posts</h2>
        <div className="space-y-4">
          {posts.map((post) => (
            <motion.div
              key={post.id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3 }}
            >
              <PostCard
                post={post}
                onLike={() => console.log('Liked post', post.id)}
                onUnlike={() => console.log('Unliked post', post.id)}
                onComment={(comment) => console.log('Commented on post', post.id, comment)}
                onDelete={() => console.log('Deleted post', post.id)}
              />
            </motion.div>
          ))}
        </div>
      </motion.div>
    </motion.div>
  );
};

export default Profile;
