import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import API_BASE_URL from '../apiConfigure';
import { motion } from 'framer-motion';

const GroupChat = () => {
  const { id } = useParams();
  const [messages, setMessages] = useState<{ id: number; sender_username: string; content: string }[]>([]);
  const [newMessage, setNewMessage] = useState('');

  useEffect(() => {
    const fetchMessages = async () => {
      const response = await axios.get<{ id: number; sender_username: string; content: string }[]>(`${API_BASE_URL}/groups/${id}/messages/`);
      setMessages(response.data);
    };

    fetchMessages();
  }, [id]);

  const handleSendMessage = async () => {
    if (!newMessage.trim()) return;

    const response = await axios.post(`${API_BASE_URL}/groups/${id}/messages/`, {
      content: newMessage,
    });
    setMessages([...messages, response.data as { id: number; sender_username: string; content: string }]);
    setNewMessage('');
  };

  return (
    <motion.div
      className="max-w-4xl mx-auto p-4 bg-white rounded-lg shadow-md"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.8 }}
    >
      <div className="space-y-4">
        {messages.map((message) => (
          <motion.div
            key={message.id}
            className="p-2 bg-gray-100 rounded-lg"
            whileHover={{ scale: 1.02 }}
          >
            <strong>{message.sender_username}:</strong> {message.content}
          </motion.div>
        ))}
      </div>
      <div className="mt-4 flex items-center">
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="Type a message..."
          className="input-field flex-1"
        />
        <motion.button
          onClick={handleSendMessage}
          className="btn-primary ml-4"
          whileHover={{ scale: 1.1 }}
        >
          Send
        </motion.button>
      </div>
    </motion.div>
  );
};

export default GroupChat;
