import React, { useEffect, useState } from 'react';
import PostCard from '../components/posts/PostCard';
import axios from 'axios';
import API_BASE_URL from '../apiConfigure';
import { motion } from 'framer-motion';

interface Post {
  id: string;
  content: string;
  image?: string;
  like_count: number;
  comment_count: number;
  createdAt: string;
  author: {
    id: string;
    name: string;
    avatar: string;
  };
}

const Home = () => {
  const [posts, setPosts] = useState<Post[]>([]);

  useEffect(() => {
    const fetchPosts = async () => {
      const response = await axios.get<Post[]>(`${API_BASE_URL}/posts/`);
      setPosts(response.data);
    };

    fetchPosts();
  }, []);

  const handleLike = async (postId: string) => {
    // Handle like functionality
  };

  const handleComment = (postId: string) => {
    // Handle comment functionality
  };

  return (
    <div className="max-w-4xl mx-auto space-y-6">
      <motion.div
        className="text-center py-10"
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <h1 className="text-4xl font-bold text-blue-600">Welcome to Campus Connect</h1>
        <p className="text-gray-600 mt-4">
          Connect with your peers, share your thoughts, and join exciting groups!
        </p>
      </motion.div>

      <motion.div
        className="space-y-6"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.5, duration: 1 }}
      >
        {posts.map((post) => (
          <motion.div
            key={post.id}
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
          >
            <PostCard
              post={post}
              onLike={handleLike}
              onComment={handleComment}
            />
          </motion.div>
        ))}
      </motion.div>
    </div>
  );
};

export default Home;
