import React, { useEffect } from 'react';
import PostCard from '../components/posts/PostCard';
import { useSelector, useDispatch } from 'react-redux';
import { AppDispatch, RootState } from '../store/store';
import { fetchPosts, toggleLike, deletePost } from '../features/posts/postsSlice';
import { motion } from 'framer-motion';

const Home = () => {
  const dispatch = useDispatch<AppDispatch>();
  const posts = useSelector((state: RootState) => state.posts.items);
  const isLoading = useSelector((state: RootState) => state.posts.isLoading);
  const error = useSelector((state: RootState) => state.posts.error);

  useEffect(() => {
    dispatch(fetchPosts());
  }, [dispatch]);

  const handleRetry = () => {
    dispatch(fetchPosts());
  };

  if (isLoading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-blue-500 border-solid"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex flex-col items-center justify-center h-screen">
        <p className="text-red-500 text-lg mb-4">Error: {error}</p>
        <button
          onClick={handleRetry}
          className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition duration-300"
        >
          Retry
        </button>
      </div>
    );
  }

  if (posts.length === 0) {
    return (
      <div className="flex flex-col items-center justify-center h-screen">
        <p className="text-gray-500 text-lg">No posts available. Create one!</p>
      </div>
    );
  }

  return (
    <motion.div
      className="max-w-4xl mx-auto space-y-6 p-4"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      {posts.map((post) => (
        <motion.div
          key={post.id}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.3 }}
        >
          <PostCard
            post={post}
            onLike={() => dispatch(toggleLike(post.id))}
            onUnlike={() => dispatch(toggleLike(post.id))}
            onComment={(comment) => console.log('Comment:', comment)}
            onDelete={() => dispatch(deletePost(post.id))}
          />
        </motion.div>
      ))}
    </motion.div>
  );
};

export default Home;
