import React from 'react';
import { motion } from 'framer-motion';

const PostsSection = () => {
  return (
    <motion.div
      className="bg-white shadow-md rounded-lg p-6"
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <h1 className="text-2xl font-bold mb-4">Posts</h1>
      <p className="text-gray-600">Display user posts and interactions here.</p>
      <div className="mt-4 space-y-4">
        <motion.div
          className="p-4 bg-gray-100 rounded-lg shadow hover:shadow-lg transition-shadow"
          whileHover={{ scale: 1.02 }}
        >
          <h2 className="text-lg font-semibold">Post Title 1</h2>
          <p className="text-sm text-gray-500">This is a sample post description.</p>
        </motion.div>
        <motion.div
          className="p-4 bg-gray-100 rounded-lg shadow hover:shadow-lg transition-shadow"
          whileHover={{ scale: 1.02 }}
        >
          <h2 className="text-lg font-semibold">Post Title 2</h2>
          <p className="text-sm text-gray-500">This is another sample post description.</p>
        </motion.div>
      </div>
    </motion.div>
  );
};

export default PostsSection;
