import React, { useState, useRef, useMemo, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import API_BASE_URL from '../apiConfigure';
import { PencilIcon, PhotoIcon, LinkIcon, UserPlusIcon, UserMinusIcon, MagnifyingGlassIcon, FunnelIcon, PlusIcon } from '@heroicons/react/24/outline';
import PostCard from '../components/posts/PostCard';
import CreatePost from '../components/posts/CreatePost';
import SharePost from '../components/posts/SharePost';

interface UserProfile {
  id: string;
  avatar: string;
  bio: string;
  firstname: string;
  lastname: string;
  university: string;
  major: string;
  year: string;
  posts: number;
  followers: number;
  following: number;
  socialLinks: {
    github?: string;
    linkedin?: string;
    twitter?: string;
    instagram?: string;
  };
}

interface Post {
  id: string;
  content: string;
  image?: string;
  likes: number;
  comments: number;
  reposts: number;
  createdAt: string;
  author: {
    id: string;
    firstname: string;
    lastname: string;
    avatar: string;
  };
  commentsList: Comment[];
  likedBy: string[];
  repostedBy: string[];
  isRepost?: boolean;
  originalPost?: {
    id: string;
    content: string;
    author: {
      id: string;
      firstname: string;
      lastname: string;
      avatar: string;
    };
  };
}

interface Comment {
  id: string;
  content: string;
  author: {
    firstname: string;
    lastname: string;
    avatar: string;
  };
  createdAt: string;
}

type SortOption = 'newest' | 'oldest' | 'mostLikes' | 'mostComments';
type FilterOption = 'all' | 'withImages' | 'withComments';

const Profile = () => {
  const { id } = useParams();
  const { user } = useAuth();
  const [isEditing, setIsEditing] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [isFollowing, setIsFollowing] = useState(false);
  const [profile, setProfile] = useState<UserProfile | null>(null);
  const [posts, setPosts] = useState<Post[]>([]);
  const [coverPhoto, setCoverPhoto] = useState<string>('https://images.unsplash.com/photo-1504805572947-34fad45aed93');
  const [profilePicture, setProfilePicture] = useState<string>(user?.avatar || 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e');
  const coverPhotoInputRef = useRef<HTMLInputElement>(null);
  const profilePictureInputRef = useRef<HTMLInputElement>(null);
  const [isCreatePostOpen, setIsCreatePostOpen] = useState(false);
  const [isSharePostOpen, setIsSharePostOpen] = useState(false);
  const [selectedPost, setSelectedPost] = useState<Post | null>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState<SortOption>('newest');
  const [filterBy, setFilterBy] = useState<FilterOption>('all');

  // Fetch profile data
  useEffect(() => {
  const fetchProfile = async () => {
    try {
      const response = await axios.get<UserProfile>(`${API_BASE_URL}/users/${id}/`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` },
      });
      setProfile(response.data);
    } catch (error) {
      console.error('Error fetching profile:', error);
    }
  };

  fetchProfile();
}, [id]);

// Fetch posts data
useEffect(() => {
  const fetchPosts = async () => {
    try {
      const response = await axios.get<Post[]>(`${API_BASE_URL}/posts/?author=${id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` },
      });
      setPosts(response.data);
    } catch (error) {
      console.error('Error fetching posts:', error);
    }
  };

  fetchPosts();
}, [id]);
  const handleFollow = async () => {
    setIsLoading(true);
    try {
      const endpoint = isFollowing
        ? `http://127.0.0.1:8000/api/users/${id}/unfollow/`
        : `http://127.0.0.1:8000/api/users/${id}/follow/`;

      await axios.post(endpoint, {}, {
        headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` },
      });

      setIsFollowing(!isFollowing);
      setProfile((prev) =>
        prev ? { ...prev, followers: isFollowing ? prev.followers - 1 : prev.followers + 1 } : null
      );
    } catch (error) {
      console.error('Error following/unfollowing:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleLike = async (postId: string) => {
    try {
      const response = await axios.post<{ likes: number }>(`http://127.0.0.1:8000/api/posts/${postId}/like/`, {}, {
        headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` },
      });

      setPosts((prev) =>
        prev.map((post) =>
          post.id === postId ? { ...post, likes: response.data.likes } : post
        )
      );
    } catch (error) {
      console.error('Error liking post:', error);
    }
  };

  const handleDeletePost = async (postId: string) => {
    if (!window.confirm('Are you sure you want to delete this post?')) return;

    setIsLoading(true);
    try {
      await axios.delete(`http://127.0.0.1:8000/api/posts/${postId}/`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('access_token')}` },
      });

      setPosts((prev) => prev.filter((post) => post.id !== postId));
      setProfile((prev) =>
        prev ? { ...prev, posts: prev.posts - 1 } : null
      );
    } catch (error) {
      console.error('Error deleting post:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const filteredAndSortedPosts = useMemo(() => {
    let result = [...posts];

    if (searchQuery) {
      result = result.filter((post) =>
        post.content.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    if (filterBy === 'withImages') {
      result = result.filter((post) => post.image);
    } else if (filterBy === 'withComments') {
      result = result.filter((post) => post.commentsList.length > 0);
    }

    switch (sortBy) {
      case 'oldest':
        result.sort((a, b) => new Date(a.createdAt).getTime() - new Date(b.createdAt).getTime());
        break;
      case 'mostLikes':
        result.sort((a, b) => b.likes - a.likes);
        break;
      case 'mostComments':
        result.sort((a, b) => b.comments - a.comments);
        break;
      default:
        result.sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime());
    }

    return result;
  }, [posts, searchQuery, filterBy, sortBy]);

  if (!profile) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      {/* Profile and Posts Section */}
      {/* Add your JSX here */}
    </div>
  );
};

export default Profile;
