import React from 'react';
import { Link } from 'react-router-dom';
import { Disclosure } from '@headlessui/react';
import { Bars3Icon, XMarkIcon } from '@heroicons/react/24/outline';
import { motion } from 'framer-motion';
import { useSelector, useDispatch } from 'react-redux';
import { RootState } from '../../store/store';
import { logout } from '../../features/auth/authSlice';
import '../../styles/components/layout/Navbar.css'; // Adjust the path as needed


const navigation = [
  { name: 'Home', href: '/' },
  { name: 'Groups', href: '/groups' },
  { name: 'Profile', href: '/profile/me' },
];

const Navbar = () => {
  const dispatch = useDispatch();
  const { user, isLoggedIn } = useSelector((state: RootState) => state.auth);

  const handleLogout = () => {
    dispatch(logout());
  };

  return (
    <Disclosure as="nav" className="bg-white shadow-sm">
      {({ open }) => (
        <>
          <div className="mx-auto max-w-7xl px-4 sm:px-6 lg:px-8">
            <div className="flex h-16 justify-between items-center">
              {/* Navigation Links on the Left */}
              <div className="hidden sm:flex sm:space-x-8">
                {navigation.map((item) => (
                  <motion.div
                    key={item.name}
                    whileHover={{ scale: 1.1 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    <Link
                      to={item.href}
                      className="inline-flex items-center px-1 pt-1 text-sm font-medium text-gray-900 hover:text-blue-600"
                    >
                      {item.name}
                    </Link>
                  </motion.div>
                ))}
              </div>

              {/* Logo with Motivational Word in the Center */}
              <motion.div
                className="absolute left-1/2 transform -translate-x-1/2 flex items-center"
                whileHover={{ scale: 1.1 }}
              >
                <Link to="/" className="text-2xl font-bold text-blue-600 flex items-center">
                  CampusConnect
                  <span className="ml-2 text-sm font-medium text-gray-500 italic">
                    Empowering Connections
                  </span>
                </Link>
              </motion.div>

              {/* Login/Register or Logout Buttons on the Right */}
              <div className="hidden sm:flex sm:items-center space-x-4">
                {isLoggedIn ? (
                  <>
                    <p className="text-gray-700">Welcome, {user?.name}</p>
                    <motion.button
                      onClick={handleLogout}
                      className="btn-secondary"
                      whileHover={{ scale: 1.1 }}
                    >
                      Logout
                    </motion.button>
                  </>
                ) : (
                  <>
                    <motion.div whileHover={{ scale: 1.1 }}>
                      <Link to="/login" className="btn-secondary">
                        Login
                      </Link>
                    </motion.div>
                    <motion.div whileHover={{ scale: 1.1 }}>
                      <Link to="/register" className="btn-primary">
                        Register
                      </Link>
                    </motion.div>
                  </>
                )}
              </div>

              {/* Mobile Menu Button */}
              <div className="-mr-2 flex items-center sm:hidden">
                <Disclosure.Button className="inline-flex items-center justify-center rounded-md p-2 text-gray-400 hover:bg-gray-100 hover:text-gray-500">
                  <span className="sr-only">Open main menu</span>
                  {open ? (
                    <XMarkIcon className="block h-6 w-6" aria-hidden="true" />
                  ) : (
                    <Bars3Icon className="block h-6 w-6" aria-hidden="true" />
                  )}
                </Disclosure.Button>
              </div>
            </div>
          </div>

          {/* Mobile Menu */}
          <Disclosure.Panel className="sm:hidden">
            <div className="space-y-1 pb-3 pt-2">
              {navigation.map((item) => (
                <Disclosure.Button
                  key={item.name}
                  as={Link}
                  to={item.href}
                  className="block py-2 pl-3 pr-4 text-base font-medium text-gray-500 hover:bg-gray-50 hover:text-gray-900"
                >
                  {item.name}
                </Disclosure.Button>
              ))}
              {isLoggedIn ? (
                <Disclosure.Button
                  as="button"
                  onClick={handleLogout}
                  className="block py-2 pl-3 pr-4 text-base font-medium text-gray-500 hover:bg-gray-50 hover:text-gray-900"
                >
                  Logout
                </Disclosure.Button>
              ) : (
                <>
                  <Disclosure.Button
                    as={Link}
                    to="/login"
                    className="block py-2 pl-3 pr-4 text-base font-medium text-gray-500 hover:bg-gray-50 hover:text-gray-900"
                  >
                    Login
                  </Disclosure.Button>
                  <Disclosure.Button
                    as={Link}
                    to="/register"
                    className="block py-2 pl-3 pr-4 text-base font-medium text-gray-500 hover:bg-gray-50 hover:text-gray-900"
                  >
                    Register
                  </Disclosure.Button>
                </>
              )}
            </div>
          </Disclosure.Panel>
        </>
      )}
    </Disclosure>
  );
};

export default Navbar;
