import React, { useState } from 'react';
import Sidebar from '../components/dashboard/Sidebar'; // Assuming Sidebar component exists
import Header from '../components/dashboard/Header';   // Assuming Header component exists
import Home from './Home'; // Example section
import Groups from './Groups'; // Example section
import Profile from './Profile'; // Example section
// Import other sections/pages as needed
import { motion, AnimatePresence } from 'framer-motion';
import '../styles/pages/Dashboard'; // Import the CSS

const Dashboard = () => {
  const [activeSection, setActiveSection] = useState('home'); // Default section

  const renderSection = () => {
    switch (activeSection) {
      case 'home':
        return <Home />;
      case 'groups':
        return <Groups />;
      case 'profile':
        return <Profile />;
      // Add cases for other sections
      // case 'admin':
      //   return <AdminPanel />; // If AdminPanel is a section here
      default:
        return <Home />;
    }
  };

  return (
    <div className="dashboard-layout">
      <Sidebar setActiveSection={setActiveSection} /> {/* Pass setActiveSection to Sidebar */}
      <div className="dashboard-main-content">
        <Header /> {/* You might want to pass user info or page title to Header */}
        <AnimatePresence mode="wait">
          <motion.div
            key={activeSection} // Key change triggers animation
            className="dashboard-section-content"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            transition={{ duration: 0.3 }}
          >
            {renderSection()}
          </motion.div>
        </AnimatePresence>
      </div>
    </div>
  );
};

export default Dashboard;
