import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { AppDispatch, RootState } from '../store/store';
import { fetchGroups, deleteGroup } from '../features/groups/groupsSlice';
import { motion } from 'framer-motion';
import Chat from '../components/dashboard/groups/Chat';
// You may need to adjust the import below based on your project structure
import { useAuth } from '../context/AuthContext'; // Assumes you have user info here

const Groups: React.FC = () => {
  const dispatch = useDispatch<AppDispatch>();
  const groups = useSelector((state: RootState) => state.groups.items);
  const isLoading = useSelector((state: RootState) => state.groups.isLoading);
  const error = useSelector((state: RootState) => state.groups.error);

  const { user } = useAuth(); // user.user_type, user.id, etc.
  const [selectedGroup, setSelectedGroup] = useState<any>(null);
  const [isMember, setIsMember] = useState(false);
  const [joining, setJoining] = useState(false);

  useEffect(() => {
    dispatch(fetchGroups());
  }, [dispatch]);

  useEffect(() => {
    if (selectedGroup && user) {
      setIsMember(selectedGroup.members?.includes(user.id));
    }
  }, [selectedGroup, user]);

  const handleJoinGroup = async (group: any) => {
    setJoining(true);
    try {
      await fetch(`${process.env.REACT_APP_API_BASE_URL || ''}/groups/${group.id}/join/`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('access_token')}`,
          'Content-Type': 'application/json',
        },
      });
      setIsMember(true);
      // Optionally update group members in state
      if (user) {
        setSelectedGroup({ ...group, members: [...(group.members || []), user.id] });
      }
    } catch (error) {
      alert('Failed to join group.');
    }
    setJoining(false);
  };

  if (isLoading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-blue-500 border-solid"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex flex-col items-center justify-center h-screen">
        <p className="text-red-500 text-lg mb-4">Error: {error}</p>
        <button
          onClick={() => dispatch(fetchGroups())}
          className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition duration-300"
        >
          Retry
        </button>
      </div>
    );
  }

  if (!groups.length) {
    return (
      <div className="flex flex-col items-center justify-center h-screen">
        <p className="text-gray-500 text-lg">No groups available. Create or join one!</p>
      </div>
    );
  }

  return (
    <motion.div
      className="max-w-4xl mx-auto space-y-6 p-4"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <h1 className="text-3xl font-bold mb-6 text-center text-blue-700">Groups</h1>
      {!selectedGroup ? (
        groups.map((group) => (
          <motion.div
            key={group.id}
            className="p-6 bg-white rounded-xl shadow-md hover:shadow-xl transition-shadow border border-gray-100"
            whileHover={{ scale: 1.02 }}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
          >
            <div className="flex flex-col md:flex-row md:items-center md:justify-between">
              <div>
                <h2 className="text-xl font-bold text-gray-800">{group.name}</h2>
                <p className="text-gray-600 mt-1">{group.description}</p>
              </div>
              <div className="flex space-x-3 mt-4 md:mt-0">
                {user?.user_type === 'student' && (
                  <>
                    {!group.members?.includes(user.id) ? (
                      <motion.button
                        className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition duration-200"
                        whileHover={{ scale: 1.07 }}
                        whileTap={{ scale: 0.97 }}
                        disabled={joining}
                        onClick={() => handleJoinGroup(group)}
                      >
                        {joining ? 'Joining...' : 'Join Group'}
                      </motion.button>
                    ) : (
                      <motion.button
                        className="px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600 transition duration-200"
                        whileHover={{ scale: 1.07 }}
                        whileTap={{ scale: 0.97 }}
                        onClick={() => setSelectedGroup(group)}
                      >
                        Open Chat
                      </motion.button>
                    )}
                  </>
                )}
                <motion.button
                  className="px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 transition duration-200"
                  whileHover={{ scale: 1.07 }}
                  whileTap={{ scale: 0.97 }}
                  onClick={() => dispatch(deleteGroup(group.id))}
                >
                  Delete Group
                </motion.button>
              </div>
            </div>
          </motion.div>
        ))
      ) : (
        <div>
          <button
            className="mb-4 px-4 py-2 bg-gray-200 rounded hover:bg-gray-300"
            onClick={() => setSelectedGroup(null)}
          >
            ← Back to Groups
          </button>
          <Chat
            groupId={selectedGroup.id}
            userType={user?.user_type ?? ''}
            isMember={isMember}
          />
        </div>
      )}
    </motion.div>
  );
};

export default Groups;
